import React, { useContext, useEffect } from 'react';
import Menu from '../component/Menu';
import Footer from '../component/Footer';
import { CardContext } from '../context/CardContext';



const Checkout=()=>{
    let user=JSON.parse(localStorage.getItem("User"))
    const {shoppingCart , qty , totalPrice }=useContext(CardContext)
    const placeOrder=(e)=>{
        e.preventDefault()
        fetch('http://localhost:5000/order',{
            method:"Post",
            headers:{
                "Content-Type":"application/json",
                "Authorization":`Bearer ${localStorage.getItem("jwt")}`
            },
            body:JSON.stringify({
                shoppingCart

            })
        }).then(res=>res.json())
        .then(arham => console.log(arham))
        .catch(err=> console.log(err))

    }

    return(
        <>
        <Menu />


        <div class="container wow fadeIn text-muted">

            {/* <!-- Heading --> */}
            <h2 class="my-5 h2 text-center">Checkout form</h2>

            {/* <!--Grid row--> */}
            <div class="row">

                {/* <!--Grid column--> */}
                <div class="col-md-8 mb-4">

                    {/* <!--Card--> */}
                    <div class="card">

                        {/* <!--Card content--> */}
                        <form class="card-body" onSubmit={placeOrder}>

                            {/* <!--Grid row--> */}
                            <div class="row">

                                {/* <!--Grid column--> */}

                                {/* <!--Grid column--> */}

                                {/* <!--Grid column--> */}
                                                                {/* <!--Grid column--> */}

                            </div>
                            {/* <!--Grid row--> */}

                            {/* <!--Username--> */}
                            <div class="md-form input-group pl-0 mb-5">
                                <div class="input-group-prepend">
                                    <span class="input-group-text" id="basic-addon1">@</span>
                                </div>
                                <input type="text" class="form-control py-0" placeholder="Username" aria-describedby="basic-addon1" value={user.name} />
                            </div>

                            {/* <!--email--> */}
                            <div class="md-form mb-5">
                                <label for="email" class="">Email (optional)</label>
                                <input type="text" id="email" class="form-control" placeholder="youremail@example.com" value={user.email} />
                            </div>

                            {/* <!--address--> */}
                            <div class="md-form mb-5">
                                <label for="address" class="">Address</label>
                                <input type="text" id="address" class="form-control" placeholder="1234 Main St" value={user.address} />
                            </div>

                            {/* <!--address-2--> */}
                            <div class="md-form mb-5">
                                <label for="address-2" class="">Address 2 (optional)</label>
                                <input type="text" id="address-2" class="form-control" placeholder="Apartment or suite" />
                            </div>

                            {/* <!--Grid row--> */}
                            <div class="row">

                                {/* <!--Grid column--> */}
                                <div class="col-lg-4 col-md-12 mb-4">

                                    <label for="country">Country</label>
                                    <select class="custom-select d-block w-100" id="country" required>
                    <option value="">Choose...</option>
                    <option>United States</option>
                  </select>
                                    <div class="invalid-feedback">
                                        Please select a valid country.
                                    </div>

                                </div>
                                {/* <!--Grid column--> */}

                                {/* <!--Grid column--> */}
                                <div class="col-lg-4 col-md-6 mb-4">

                                    <label for="state">State</label>
                                    <select class="custom-select d-block w-100" id="state" required>
                    <option value="">Choose...</option>
                    <option>California</option>
                  </select>
                                    <div class="invalid-feedback">
                                        Please provide a valid state.
                                    </div>

                                </div>
                                {/* <!--Grid column--> */}

                                {/* <!--Grid column--> */}
                                <div class="col-lg-4 col-md-6 mb-4">

                                    <label for="zip">Zip</label>
                                    <input type="text" class="form-control" id="zip" placeholder="" required />
                                    <div class="invalid-feedback">
                                        Zip code required.
                                    </div>

                                </div>
                                {/* <!--Grid column--> */}

                            </div>
                            {/* <!--Grid row--> */}

                            <hr />
